    public View onCreateView(LayoutInflater layoutInflater, @Nullable ViewGroup viewGroup, @Nullable Bundle bundle) {
        View inflate = layoutInflater.inflate(R.layout.fragment_privacy_policy, viewGroup, false);
        ButterKnife.bind((Object) this, inflate);
        if (Build.VERSION.SDK_INT >= 19) {
            this.webView.setLayerType(2, (Paint) null);
        } else {
            this.webView.setLayerType(1, (Paint) null);
        }
        this.webView.loadData(this.privacy_policy, AudienceNetworkActivity.WEBVIEW_MIME_TYPE, "UTF-8");
        this.webView.loadUrl("file:///android_asset/privacypolicy.html");
        this.bClose.setOnClickListener(new View.OnClickListener() {
            public void onClick(View view) {
                FragmentPrivacyPolicy.this.dismiss();
            }
        });
        return inflate;
    }
